import {all,fork,call,put,take,delay} from 'redux-saga/effects';
import {LOG_IN,LOG_OUT,LOG_IN_SUCCESS} from '../reducers/user';

const HELLO_SAGA  = "helloSaga";


function loginAPI()
{
    //서버에 요청을 보내는 부분
}


function* login()
{
    try{
    //    yield call(loginAPI);
    yield delay(100);
        yield put({
            type: LOG_IN_SUCCESS
        })
    } catch(e)
    {
        Console.error(e);
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* watchLogin()
{
    yield take(LOG_IN);
    yield put({
        type: LOG_IN_SUCCESS,
    });
}

function watchSignUp()

{

}




export default function* userSaga()
{
     yield all([
       
         watchLogin(),
         watchSignUp(),
     ]);
}
