import React from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import reducer from '../reducers';
import {Provider} from '/react-redux';
import withRedux from 'next-redux-wrapper';
import {createStore}  from 'redux';
const NodeBird = ({Component,store}) =>
{
    return (
        <Provider store={store}>
            <Head>
                  <title>NodeBird</title>
                    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.6/antd.css"/>
                    <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.6/antd.js" />
            </Head>
        
            <AppLayout>
                <Component />
           
            </AppLayout>
        </Provider>
    )
};



export default withRedux((initalState,options)=>
{
    const sotre = createStore(reducer,initalState);
    return store;
})(NodeBird);
