import React from 'react';

import {Card, Avatar} from 'antd';
import {useSelector} from 'react-redux';
import {LOG_IN,LOG_OUT,loginAction,logoutAction} from '../reducers/post'


const UserProfile = () =>
{
    const {Post,Followers,Followings,user} = useSelector(state=>state);


            <Card

            actions={[
                <div key="twit">짹짹<br />{Post.length}</div>,
                <div key="following">팔로잉<br />{Followings.length}</div>,
                <div key="follower">팔로워<br />{Followers.length}</div>,
                

            ]}
            >
            <Card.Meta
                avatar={<Avatar src="https://images.alphacoders.com/849/849018.png">{user.nickname[0]}</Avatar>}
                title={user.nickname}
            />
            </Card>
}

export default UserProfile;
