import React,{useEffect} from 'react';
import { Input , Form, Button, Card, Icon, Avatar} from 'antd';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { useDispatch,useSelector } from 'react-redux';
import {LOG_IN,LOG_OUT,loginAction,logoutAction} from '../reducers/user'
const dummy = {
    isLoggedIn: true,
    imagePath: [],
    mainPosts: [{
        User:{
            id: 1,
            nickname: '제로초',
        },
        content: '첫 번째 게시글',
        img: 'https://wallpapercave.com/wp/wp2195776.jpg',

    }],
};

const Home = () =>
{
    const dispatch = useDispatch();
    useEffect(()=>
    {
        dispatch(loginAction);
        dispatch(logoutAction);
      
    })
    const user = useSelector(state=>state.user)
    console.log(user);

    return(
        <div>
          {user ? <div>로그인 했습니다 :  {user.nickname} </div>: <div>로그아웃했습니다.</div>}
            {dummy.isLoggedIn&& <PostForm />}
                {dummy.mainPosts.map((c) =>
                {
                    return(
                          <PostCard key={c} post={c} />  
                    )
                })
            }
            

        </div>
    )}

export default Home;
