import {all,fork,call,put,take,} from 'redux-saga/effects';
import {LOG_IN,LOG_OUT,LOG_IN_SUCCESS} from '../reducers/user';

const HELLO_SAGA  = "helloSaga";


function loginAPI()
{
    //서버에 요청을 보내는 부분
}


function* login()
{
    try{
        yield call(loginAPI);
        yield put({
            type: LOG_IN_SUCCESS
        })
    } catch(e)
    {
        Console.error(e);
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* watchLogin()
{
    yield takeLatest(LOG_IN,login);
}

function* helloSaga()
{
    console.log("hello saga");
    yield take(HELLO_SAGA);
}


export default function* useSaga()
{
     yield   helloSaga()
}
