import React from 'react';
import { Input , Form, Button, Card, Icon, Avatar} from 'antd';
const dummy = {
    isLoggedIn: true,
    imagePath: [],
    mainPosts: [{
        User:{
            id: 1,
            nickname: '제로초',
        },
        content: '첫 번째 게시글',

    }],
};

const Home = () =>
{
    return(
        <div>
            {dummy.isLoggedIn&& <Form style={{marginBottom: 20}}encType="multipart/form-data">
                <Input.TextArea maxLength={140} style={{marginTop: 20}} placeholder="어떤 신기한 일이 있었나요?" />
                <div>
                    <input type="file" multiple hidden />
                    <Button>이미지 업로드</Button>
                    <Button type="primary" style={{float: 'right'}} htmlType="submit">짹짹</Button>
                
                </div>
                <div>
                    {dummy.imagePath.map((v,i)=>
                    {
                        return(
                            <div key={v} style={{display: 'inline-block'}}>
                                <img src={'https://localhost:3000/' + v} style={{width: '200px'}} alt={v}/>
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                    );
                    })}
                    
                </div>
                </Form>}
                {dummy.mainPosts.map((c) =>
                {
                    return(
                        <Card
                        Key={c.createAt}
                        cover={c.img && <img alt="example" src={c.img} />}
                        actions ={[
                            <Icon type="retweet" key="retweet" />,
                            <Icon type="heart" key="heart" />,
                            <Icon type="message" key="message" />,
                            <Icon type="ellipsis" key="ellipsis" />,

                        ]}
                        extra={<Button>팔로우</Button>}
                        >
                        <Card.Meta avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                        title={c.User.nickname}
                        description={c.content}
                        />
                        </Card>

                    )
                })
            }
            

        </div>
    )}

export default Home;
