export const initialState ={
    isLoggedIn: false,
    user: {},

};

export const LOG_IN = 'LOG_IN'; // name of action
export const LOG_OUT = 'LOG_OUT'
export const loginAction ={ //  Action
    type: LOG_IN,
    isLoggedIn: true,

    data: {
        nickname: '제로초',
    }
};


export const logoutAction = {
    type: LOG_OUT,

    data: {
        nickname: '',

    }

}

 const reducer = (state = initialState, action) => // reducer
{
    switch(action.type)
    {
         case LOG_IN:
            {
                return {
                    ...state,
                    isLoggedIn: true,
                    user: action.data,
                };
            }
        case LOG_OUT:
            {
                return{
                    ...state,
                    isLoggedIn: false,
                    user: action.data,

                };
            }
         default:
             {
                 return{
                     ...state,
                 };
             }
                

    }
};

export default reducer
