import React,{useState} from 'react';
import AppLayout from '../components/AppLayout';
import Head from 'next/head';
import { Form, Input,Checkbox,Button } from 'antd';
const signup = () =>
{
    const [id,setId] = useState('');

    const [nick,setNick] = useState('');
    const [password,setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState('');
    const [term,setTerm] = useState(false);

    const onSubmit = () => {
        e.preventDefault();
        console.llog({
            id,
            nick,
             
        })
    }

    const onChangeID = (e) => {
        setId(e.target.value);
    };

    const onChangeNick = (e) => {
        setNick(e.target.value);
    };

    const onChangePassword = (e) => {
        setPassword(e.target.value);
    };
    
    const onChangePasswordCheck = (e) =>
    {
            setPasswordCheck(e.target.value);
    }
    const onChangeTerm = (e) => {
        setTerm(e.target.value);
    };

    

    return(
        <>
        <Head>
            <title></title>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.css"/>
        </Head>
            <AppLayout>
                <Form onSubmit={onSubmit} style={{padding:30}}>
                    <div>
                        <label htmlFor="user-id">아이디</label>
                        <br />
                        <Input name="user-id" value={id} required onChange={onChangeID}></Input>
                    </div>

                    <div>
                        <label htmlFor="user-nick">닉네임</label>
                        <br />
                        <Input name="user-nick" value={nick} required onChange={onChangeNick}></Input>
                    </div>

                    <div>
                        <label htmlFor="user-pass">패스워드</label>
                        <br />
                        <Input name="user-pass" value={password} required onChange={onChangePassword}></Input>
                    </div>
                    <div>

                        <label htmlFor="user-passCheck">패스워드 체크</label>
                        <br />
                        <Input name="user-passCheck" value={passwordCheck} required onChange={onChangePasswordCheck}></Input>
                    </div>
                <div>
                    <Checkbox name="user-term"  value={term} onChange={onChangeTerm}>종근이의 말을 잘 들을 것에 동의합니다.</Checkbox>
                </div>

                <div>
                    <Button type="primary"  htmlType="submit">가입하기</Button>
                </div>

                </Form>
            </AppLayout>
        </>
    )
};

export default signup;
