import {all,fork,call,put} from 'redux-saga/effects';
import {LOG_IN,LOG_OUT,LOG_IN_SUCCESS} from '../reducers/user';

function loginAPI()
{
    //서버에 요청을 보내는 부분
}


function* login()
{
    try{
        yield call(loginAPI);
        yield put({
            type: LOG_IN_SUCCESS
        })
    } catch(e)
    {
        Console.error(e);
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* watchLogin()
{
    yield takeLatest(LOG_IN,login);
}

export default function* useSaga()
{
    yield all([fork(watchLogin),
    ]);
}
