import React,{  } from 'react';
import {Form, Menu,Input, Button, Col,Row,Card,Avatar}  from 'antd';
import Link from 'next/link';
const dummy ={
    nickname: '최종근',
    Post: [],
    Followings: [],
    Followers: []
};

const [userID,setUserID] = useState('');
const [userPassword,setUserPassword] = useState('');
const onChangeUserID = (e) =>{

    setUserID(    
        e.target.value
        )
}
const onChangeUserPassword =(e) =>
{ 
    setUserPassword(    e.target.value)
}
const AppLayout = ({children}) =>
{
    return(
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                  <Input.Search enterButton style={{verticalAlign: 'middle'}}/>
               </Menu.Item>
            </Menu>
            <Row>
    	<Col xs={24} md={6} >
        <Card

            actions={[
                <div key="twit">짹짹<br />{dummy.Post.length}</div>,
                <div key="following">팔로잉<br />{dummy.Followings.length}</div>,
                <div key="follower">팔로워<br />{dummy.Followers.length}</div>,
                

            ]}
            >
            <Card.Meta
                avatar={<Avatar src="https://images.alphacoders.com/849/849018.png">{dummy.nickname[0]}</Avatar>}
                title={dummy.nickname}
            />
         </Card>
         <Form>
             <div>
                 <label htmlFor="user-id" >아이디</label>
                 <br />
                 <Input name="user-id" value={userID} onChange={onChangeUserID} required></Input>
             </div>
             <div>
                 <label htmlFor="user-password">비밀번호</label>
                 <br />
                 <Input name="user-password" value={userPassword} type="password" onChange={onChangeUserPassword} required></Input>
             </div>
             <div>
                 <Button type="primary" htmlType="submit" loading={false}>로그인</Button>
                 <Button><Link href="/signup"><a>회원가입</a></Link></Button>
             </div>
         </Form>

        </Col>
		<Col xs={24} md={12} >   {children}</Col>
    	<Col xs={24} md={6} >세번째</Col>

</Row>
         
        </div>
    )
};

export default AppLayout;
