import React from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import reducer from '../reducers';
import { applyMiddleware, compose, createStore } from 'redux';
import withRedux from 'next-redux-wrapper';
import {Provider} from 'react-redux';
import sagaMiddleware from '../sagas/middleware'; 
import rootSage from '../sagas';

const NodeBird = ({Component,store}) =>
{
    return (
        <Provider store={store}>
            <Head>
                  <title>NodeBird</title>
                    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.6/antd.css"/>
                    <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.6/antd.js" />
            </Head>

            <AppLayout>
                <Component />
           
            </AppLayout>
        </Provider>
    )
};



export default withRedux((initalState,options)=>
{

    const middlewares = [sagaMiddleware];
    const enhancer = compose(applyMiddleware(...middlewares),
    !options.isServer && typeof window.__REDUX_DEVTOOL  
    );
    
    constÂ store = createStore(reducer, initalState, enhancer);
    return store;
    sagaMiddleware.run(rootSaga);
})(NodeBird);
